package main;

public class Tripla {
    private String n1;
    private String n2;
    private String predicado;

    public Tripla(String n1, String n2, String predicado) {
        this.n1 = n1;
        this.n2 = n2;
        this.predicado = predicado;
    }

	public String getn1() {
		// TODO Auto-generated method stub
		return n1;
	}

	public String getn2() {
		// TODO Auto-generated method stub
		return n2;
	}

	public String getPredicado() {
		// TODO Auto-generated method stub
		return predicado;
	}

}
